mutation CreateUser($name: String!, $username: String!, $email: String!, $street: String!, $suite: String!, $city: String!, $zipcode: String!, $lat: Float!, $lng: Float!, $phone: String!, $website: String!, $companyName: String!, $companyCatchPhrase: String!) {
  createUser(
    input: {name: $name, username: $username, email: $email, address: {street: $street, suite: $suite, city: $city, zipcode: $zipcode, geo: {lat: $lat, lng: $lng}}, phone: $phone, website: $website, company: {name: $companyName, catchPhrase: $companyCatchPhrase}}
  ) {
    id
    name
    username
    email
    phone
    website
    company {
      name
    }
  }
}

query GetUser($userid: ID!) {
  user(id: $userid) {
    id
    name
    email
    posts {
      data {
        id
        title
      }
    }
    address {
      city
      street
      zipcode
    }
  }
}

query GetUserPosts($userid: ID!) {
    user(id: $userid) {
        name
        username
        email
        phone
        website
        id
        posts {
            data {
                id
                title
                body
            }
        }
    }
}


query GetUserAlbums($userid: ID!) {
    user(id: $userid) {
        id
        name
        username
        email
        phone
        website
        albums {
            data {
                id
                title
            }
        }
    }
}

