rq.test("Response status code is 200", function () {
  rq.response.to.have.status(200);
});


rq.test("Response is an array", function () {
  const responseData = rq.response.json();

  rq.expect(responseData).to.be.an('array');
});


rq.test("Response array should contain at least one element", function () {
  const responseData = rq.response.json();

  rq.expect(responseData).to.be.an('array').and.to.have.lengthOf.at.least(1);
});


rq.test("Response array elements are integers", function () {
  const responseData = rq.response.json();

  rq.expect(responseData).to.be.an('array');
  responseData.forEach(function (element) {
    rq.expect(element).to.be.a('number');
  });
});


rq.test("Response time is less than 500ms", function () {
  rq.expect(rq.response.responseTime).to.be.below(500);
});

